import React from 'react';
import { useNavigate } from 'react-router-dom';
import PageContainer from '../components/layout/PageContainer';
import ReportUploader from '../components/reports/ReportUploader';
import UploadProgress from '../components/reports/UploadProgress';
import { useState } from 'react';
import Card from '../components/common/Card';
import Button from '../components/common/Button';

const ReportUploadPage: React.FC = () => {
  const [uploadedReportId, setUploadedReportId] = useState<string | null>(null);
  const [uploadComplete, setUploadComplete] = useState<boolean>(false);
  const navigate = useNavigate();

  // Handle upload success
  const handleUploadSuccess = (reportId: string) => {
    setUploadedReportId(reportId);
  };

  // Handle processing complete
  const handleProcessingComplete = () => {
    setUploadComplete(true);
  };

  // Handle view report
  const handleViewReport = () => {
    if (uploadedReportId) {
      navigate(`/reports/${uploadedReportId}`);
    }
  };

  // Handle upload another
  const handleUploadAnother = () => {
    setUploadedReportId(null);
    setUploadComplete(false);
  };

  // Handle go to reports list
  const handleGoToReports = () => {
    navigate('/reports');
  };

  return (
    <PageContainer
      title="Upload Blood Test Report"
      description="Upload your blood test report to get insights and recommendations"
    >
      <div className="max-w-3xl mx-auto">
        {!uploadedReportId ? (
          <ReportUploader onUploadSuccess={handleUploadSuccess} />
        ) : !uploadComplete ? (
          <UploadProgress 
            reportId={uploadedReportId} 
            onComplete={handleProcessingComplete} 
          />
        ) : (
          <Card>
            <div className="p-6 text-center">
              <div className="w-16 h-16 bg-success bg-opacity-10 rounded-full flex items-center justify-center mx-auto mb-4">
                <svg className="w-8 h-8 text-success" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5 13l4 4L19 7"></path>
                </svg>
              </div>
              <h3 className="text-xl font-semibold text-neutral-800 dark:text-white mb-2">
                Report Processed Successfully
              </h3>
              <p className="text-neutral-600 dark:text-neutral-300 mb-6">
                Your blood test report has been successfully processed. You can now view the detailed analysis, insights, and recommendations.
              </p>
              <div className="flex flex-col sm:flex-row gap-3 justify-center">
                <Button
                  variant="primary"
                  onClick={handleViewReport}
                >
                  View Report
                </Button>
                <Button
                  variant="outline"
                  onClick={handleUploadAnother}
                >
                  Upload Another Report
                </Button>
                <Button
                  variant="text"
                  onClick={handleGoToReports}
                >
                  Go to Reports List
                </Button>
              </div>
            </div>
          </Card>
        )}
      </div>
    </PageContainer>
  );
};

export default ReportUploadPage;
