import React, { useState, useEffect } from 'react';
import { useReports } from '../context/ReportsContext';
import { useUser } from '../context/UserContext';
import PageContainer from '../components/layout/PageContainer';
import Card from '../components/common/Card';
import Button from '../components/common/Button';
import HealthScoreGauge from '../components/charts/HealthScoreGauge';
import CategoryDistributionChart from '../components/charts/CategoryDistributionChart';
import { useNavigate } from 'react-router-dom';
import { Report } from '../types/Report';

const Dashboard: React.FC = () => {
  const { reports, isLoading, error, fetchReports } = useReports();
  const { user } = useUser();
  const navigate = useNavigate();
  const [healthScore, setHealthScore] = useState<number>(0);
  const [recentReports, setRecentReports] = useState<Report[]>([]);
  const [abnormalCategoriesData, setAbnormalCategoriesData] = useState<Array<{name: string, value: number, color?: string}>>([]);

  // Calculate health score and prepare chart data
  useEffect(() => {
    if (reports.length > 0) {
      // Sort reports by date (newest first)
      const sortedReports = [...reports].sort((a, b) => {
        return new Date(b.report_info.report_date).getTime() - new Date(a.report_info.report_date).getTime();
      });

      // Get recent reports (up to 3)
      setRecentReports(sortedReports.slice(0, 3));

      // Calculate health score based on most recent report
      const latestReport = sortedReports[0];
      const totalParameters = latestReport.test_sections.reduce(
        (sum, section) => sum + section.parameters.length, 
        0
      );
      const abnormalCount = latestReport.abnormal_parameters.length;
      const normalCount = totalParameters - abnormalCount;
      const score = Math.round((normalCount / totalParameters) * 100);
      setHealthScore(score);

      // Prepare abnormal categories data
      const categoryCounts: Record<string, number> = {};

      latestReport.abnormal_parameters.forEach(param => {
        const category = param.section;
        categoryCounts[category] = (categoryCounts[category] || 0) + 1;
      });

      const categoryColors: Record<string, string> = {
        'Lipid Panel': '#3B82F6',
        'Complete Blood Count': '#10B981',
        'Metabolic Panel': '#FBBF24',
        'Liver Function': '#EF4444',
        'Kidney Function': '#8B5CF6',
        'Thyroid Panel': '#EC4899',
        'Electrolytes': '#14B8A6',
        'Vitamins & Minerals': '#F97316',
      };

      const chartData = Object.entries(categoryCounts).map(([name, value]) => ({
        name,
        value,
        color: categoryColors[name] || undefined,
      }));

      setAbnormalCategoriesData(chartData);
    }
  }, [reports]);

  // Handle refresh
  const handleRefresh = () => {
    fetchReports();
  };

  // Handle upload new report
  const handleUploadNew = () => {
    navigate('/upload');
  };

  // Format date
  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return new Intl.DateTimeFormat('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
    }).format(date);
  };

  return (
    <PageContainer
      title={`Welcome${user ? ', ' + user.name : ''}`}
      description="Your health insights dashboard"
      isLoading={isLoading}
      error={error}
      actions={
        <>
          <Button
            variant="outline"
            onClick={handleRefresh}
            leftIcon={
              <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15"></path>
              </svg>
            }
          >
            Refresh
          </Button>
          <Button
            variant="primary"
            onClick={handleUploadNew}
            leftIcon={
              <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 4v16m8-8H4"></path>
              </svg>
            }
          >
            Upload New Report
          </Button>
        </>
      }
    >
      {reports.length === 0 ? (
        <div className="text-center py-12">
          <svg className="w-16 h-16 text-neutral-400 mx-auto mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"></path>
          </svg>
          <h3 className="text-xl font-semibold text-neutral-800 dark:text-white mb-2">No Reports Found</h3>
          <p className="text-neutral-600 dark:text-neutral-400 mb-6">
            Upload your first blood test report to get started with personalized health insights.
          </p>
          <Button
            variant="primary"
            onClick={handleUploadNew}
          >
            Upload Your First Report
          </Button>
        </div>
      ) : (
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Health Score */}
          <div className="lg:col-span-1">
            <HealthScoreGauge 
              score={healthScore}
              title="Overall Health Score"
              description="Based on your most recent blood test report"
            />
          </div>

          {/* Abnormal Categories */}
          <div className="lg:col-span-2">
            <CategoryDistributionChart 
              data={abnormalCategoriesData}
              title="Abnormal Parameters by Category"
              description="Distribution of abnormal parameters in your most recent report"
            />
          </div>

          {/* Recent Reports */}
          <div className="lg:col-span-2">
            <Card>
              <div className="p-6">
                <div className="flex justify-between items-center mb-4">
                  <h3 className="text-lg font-semibold text-neutral-800 dark:text-white">
                    Recent Reports
                  </h3>
                  <Button
                    variant="text"
                    size="sm"
                    onClick={() => navigate('/reports')}
                  >
                    View All
                  </Button>
                </div>

                <div className="space-y-4">
                  {recentReports.map((report) => (
                    <div 
                      key={report.id}
                      className="bg-neutral-50 dark:bg-neutral-900 rounded-lg p-4 hover:bg-neutral-100 dark:hover:bg-neutral-800 transition-colors cursor-pointer"
                      onClick={() => navigate(`/reports/${report.id}`)}
                    >
                      <div className="flex justify-between items-start">
                        <div>
                          <h4 className="font-medium text-neutral-800 dark:text-white">
                            {report.report_info.report_type}
                          </h4>
                          <p className="text-sm text-neutral-600 dark:text-neutral-400 mt-1">
                            {formatDate(report.report_info.report_date)} â€¢ {report.report_info.lab_name}
                          </p>
                        </div>
                        <div className="flex items-center">
                          <span className="text-sm text-neutral-700 dark:text-neutral-300 mr-2">
                            {report.abnormal_parameters.length} abnormal
                          </span>
                          <svg className="w-5 h-5 text-neutral-400" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 5l7 7-7 7"></path>
                          </svg>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </Card>
          </div>

          {/* Quick Actions */}
          <div className="lg:col-span-1">
            <Card>
              <div className="p-6">
                <h3 className="text-lg font-semibold text-neutral-800 dark:text-white mb-4">
                  Quick Actions
                </h3>

                <div className="space-y-3">
                  <Button
                    variant="outline"
                    fullWidth
                    onClick={() => navigate('/upload')}
                    leftIcon={
                      <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M7 16a4 4 0 01-.88-7.903A5 5 0 1115.9 6L16 6a5 5 0 011 9.9M15 13l-3-3m0 0l-3 3m3-3v12"></path>
                      </svg>
                    }
                  >
                    Upload New Report
                  </Button>

                  <Button
                    variant="outline"
                    fullWidth
                    onClick={() => navigate('/insights')}
                    leftIcon={
                      <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"></path>
                      </svg>
                    }
                  >
                    View Insights
                  </Button>

                  <Button
                    variant="outline"
                    fullWidth
                    onClick={() => navigate('/recommendations')}
                    leftIcon={
                      <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2"></path>
                      </svg>
                    }
                  >
                    View Recommendations
                  </Button>

                  <Button
                    variant="outline"
                    fullWidth
                    onClick={() => navigate('/history')}
                    leftIcon={
                      <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z"></path>
                      </svg>
                    }
                  >
                    View History
                  </Button>
                </div>
              </div>
            </Card>
          </div>
        </div>
      )}
    </PageContainer>
  );
};

export default Dashboard;
