import React, { useState } from 'react';
import { useReports } from '../context/ReportsContext';
import PageContainer from '../components/layout/PageContainer';
import RecommendationsList from '../components/recommendations/RecommendationsList';
import Card from '../components/common/Card';
import Button from '../components/common/Button';
import { useNavigate } from 'react-router-dom';
import { Recommendation } from '../types/Report';

const RecommendationsPage: React.FC = () => {
  const { recommendations, isLoading, error } = useReports();
  const navigate = useNavigate();

  // Handle recommendation click
  const handleRecommendationClick = (recommendation: Recommendation) => {
    navigate(`/recommendations/${recommendation.id}`);
  };

  return (
    <PageContainer
      title="Health Recommendations"
      description="Personalized recommendations based on your blood test results"
      isLoading={isLoading}
      error={error}
      actions={
        <Button
          variant="primary"
          onClick={() => navigate('/upload')}
          leftIcon={
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 4v16m8-8H4"></path>
            </svg>
          }
        >
          Upload New Report
        </Button>
      }
    >
      {recommendations.length === 0 && !isLoading ? (
        <div className="text-center py-12">
          <svg className="w-16 h-16 text-neutral-400 mx-auto mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2"></path>
          </svg>
          <h3 className="text-xl font-semibold text-neutral-800 dark:text-white mb-2">No Recommendations Available</h3>
          <p className="text-neutral-600 dark:text-neutral-400 mb-6">
            Upload a blood test report to get personalized health recommendations.
          </p>
          <Button
            variant="primary"
            onClick={() => navigate('/upload')}
          >
            Upload Report
          </Button>
        </div>
      ) : (
        <RecommendationsList 
          recommendations={recommendations}
          isLoading={isLoading}
          error={error}
          onRecommendationClick={handleRecommendationClick}
        />
      )}
    </PageContainer>
  );
};

export default RecommendationsPage;
