import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useReports } from '../context/ReportsContext';
import PageContainer from '../components/layout/PageContainer';
import Card from '../components/common/Card';
import Button from '../common/Button';
import Tabs from '../components/common/Tabs';
import ReportPreview from '../components/reports/ReportPreview';
import ParameterBarChart from '../components/charts/ParameterBarChart';
import ParameterLineChart from '../components/charts/ParameterLineChart';
import InsightsList from '../components/insights/InsightsList';
import RecommendationsList from '../components/recommendations/RecommendationsList';
import { Report, Parameter } from '../types/Report';

const ReportDetailPage: React.FC = () => {
  const { reportId } = useParams<{ reportId: string }>();
  const { reports, getReportById, isLoading, error } = useReports();
  const [report, setReport] = useState<Report | null>(null);
  const [historicalReports, setHistoricalReports] = useState<Report[]>([]);
  const navigate = useNavigate();

  // Fetch report data
  useEffect(() => {
    if (reportId) {
      const currentReport = getReportById(reportId);
      if (currentReport) {
        setReport(currentReport);

        // Get historical reports for the same patient
        const patientReports = reports.filter(r => 
          r.patient_info.patient_id === currentReport.patient_info.patient_id &&
          r.id !== currentReport.id
        );

        // Sort by date (newest first)
        const sortedReports = patientReports.sort((a, b) => 
          new Date(b.report_info.report_date).getTime() - new Date(a.report_info.report_date).getTime()
        );

        setHistoricalReports(sortedReports);
      }
    }
  }, [reportId, reports, getReportById]);

  // Handle back to reports
  const handleBackToReports = () => {
    navigate('/reports');
  };

  // Format date
  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return new Intl.DateTimeFormat('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
    }).format(date);
  };

  // Prepare data for parameter bar chart
  const prepareBarChartData = () => {
    if (!report) return [];

    return report.abnormal_parameters.map(param => {
      // Extract reference range min and max
      let min = 0;
      let max = 100;

      if (param.reference_range) {
        const rangeMatch = param.reference_range.match(/(\d+\.?\d*)\s*-\s*(\d+\.?\d*)/);
        if (rangeMatch) {
          min = parseFloat(rangeMatch[1]);
          max = parseFloat(rangeMatch[2]);
        }
      }

      return {
        name: param.name,
        value: parseFloat(param.value),
        min,
        max,
        isAbnormal: true,
      };
    });
  };

  // Prepare data for parameter line chart
  const prepareLineChartData = (paramName: string) => {
    if (!report || historicalReports.length === 0) return [];

    // Combine current report with historical reports
    const allReports = [report, ...historicalReports];

    // Extract parameter values from all reports
    return allReports.map(r => {
      let paramValue: number | null = null;
      let referenceMin: number | null = null;
      let referenceMax: number | null = null;
      let isAbnormal = false;

      // Find the parameter in the report
      r.test_sections.forEach(section => {
        section.parameters.forEach(param => {
          if (param.name === paramName) {
            // Convert value to number
            paramValue = parseFloat(param.value);

            // Extract reference range min and max
            if (param.reference_range) {
              const rangeMatch = param.reference_range.match(/(\d+\.?\d*)\s*-\s*(\d+\.?\d*)/);
              if (rangeMatch) {
                referenceMin = parseFloat(rangeMatch[1]);
                referenceMax = parseFloat(rangeMatch[2]);
              }
            }
          }
        });
      });

      // Check if parameter is in abnormal parameters
      isAbnormal = r.abnormal_parameters.some(p => p.name === paramName);

      return {
        date: r.report_info.report_date,
        value: paramValue || 0,
        referenceMin,
        referenceMax,
        isAbnormal,
      };
    }).filter(item => item.value !== 0);
  };

  // Create tabs for the report detail view
  const reportTabs = report ? [
    {
      id: 'overview',
      label: 'Overview',
      content: (
        <div className="py-4">
          <ReportPreview report={report} />
        </div>
      ),
    },
    {
      id: 'abnormal',
      label: 'Abnormal Parameters',
      content: (
        <div className="py-4">
          <ParameterBarChart 
            data={prepareBarChartData()}
            title="Abnormal Parameters"
            description="Parameters outside the normal reference range"
          />

          {report.abnormal_parameters.length > 0 && historicalReports.length > 0 && (
            <div className="mt-6">
              <h3 className="text-lg font-semibold text-neutral-800 dark:text-white mb-4">
                Historical Trends
              </h3>
              <div className="space-y-6">
                {report.abnormal_parameters.slice(0, 3).map((param) => (
                  <ParameterLineChart
                    key={param.name}
                    data={prepareLineChartData(param.name)}
                    title={param.name}
                    description={`Historical values for ${param.name}`}
                    parameterName={param.name}
                    unit={param.unit || ''}
                  />
                ))}
              </div>
            </div>
          )}
        </div>
      ),
    },
    {
      id: 'insights',
      label: 'Insights',
      content: (
        <div className="py-4">
          <InsightsList 
            insights={report.insights || []}
            reports={[report]}
          />
        </div>
      ),
    },
    {
      id: 'recommendations',
      label: 'Recommendations',
      content: (
        <div className="py-4">
          <RecommendationsList 
            recommendations={report.recommendations || []}
          />
        </div>
      ),
    },
  ] : [];

  return (
    <PageContainer
      title={report ? `${report.report_info.report_type}` : 'Report Details'}
      description={report ? `${formatDate(report.report_info.report_date)} • ${report.report_info.lab_name}` : 'Loading report details...'}
      isLoading={isLoading}
      error={error}
      actions={
        <Button
          variant="outline"
          onClick={handleBackToReports}
          leftIcon={
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 19l-7-7m0 0l7-7m-7 7h18"></path>
            </svg>
          }
        >
          Back to Reports
        </Button>
      }
    >
      {report ? (
        <Card>
          <Tabs tabs={reportTabs} />
        </Card>
      ) : !isLoading && !error ? (
        <div className="text-center py-12">
          <svg className="w-16 h-16 text-neutral-400 mx-auto mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"></path>
          </svg>
          <h3 className="text-xl font-semibold text-neutral-800 dark:text-white mb-2">Report Not Found</h3>
          <p className="text-neutral-600 dark:text-neutral-400 mb-6">
            The report you're looking for doesn't exist or has been removed.
          </p>
          <Button
            variant="primary"
            onClick={handleBackToReports}
          >
            Go to Reports List
          </Button>
        </div>
      ) : null}
    </PageContainer>
  );
};

export default ReportDetailPage;
